    private void InsertCmbModele(){
        String t[] = {"ModeleV"};
        rs = db.fcSelectCommand(t ,"typevoitures", " MarqueV ='"+ cmb_marq.getSelectedItem().toString() + "'" );
        try{
            cmb_mod.removeAllItems();
            while(rs.next()){
                cmb_mod.addItem(rs.getString("ModeleV"));
                }
        }catch(SQLException ex){
            System.out.println(ex.getMessage());
        }
    }
    
    
    
